con:config.script:
// Clear the console for first time run
com.eviware.soapui.SoapUI.logMonitor.getLogArea("script log").clear();

// Set the shorten objects
def project = testRunner.testCase.testSuite.project;
def testCase = testRunner.testCase;
def testSuite = testRunner.testCase.testSuite;

// Set the URL
/*
def hostName = project.getPropertyValue("hostName");
def endPoint = testCase.getPropertyValue("endPoint");
def URL = "http://" + hostName + endPoint;
testCase.getTestStepByName("Send").getHttpRequest().setEndpoint(URL);
log.info ("addRecipient_BothToken URL:" + URL);
*/

//Get the current First Name & Lastname and increment the number part
def newFirstName = project.getPropertyValue("nextFirstName");
def newLastName = project.getPropertyValue("nextLastName");
def intVal = newFirstName.split(" ")[1].toLong()+1;
newFirstName = newFirstName.split(" ")[0] + " " + intVal;
newLastName = newLastName.split(" ")[0] + " " + intVal;
project.setPropertyValue("nextFullName",newFirstName.replaceAll(" ","") + " "+ newLastName.replaceAll(" ",""));
project.setPropertyValue("nextFirstName",newFirstName);
project.setPropertyValue("nextLastName",newLastName);
log.info("Name: "+project.getPropertyValue("nextFullName"));

// Get the current email token value and add 1 into it
def newEmailToken = project.getPropertyValue("nextEmailToken")
def count;
if(!newEmailToken || newEmailToken.empty){
	newEmailToken = project.getPropertyValue("nextFirstName").replaceAll(" ",".") + "@email.com";
} else {
    	count = newEmailToken.split("@")[0].split("\\.")[1].toLong()+1;
  	newEmailToken = project.getPropertyValue("nextFirstName").split(" ")[0] + "." + count + "@email.com";
}
project.setPropertyValue("nextEmailToken",newEmailToken);
log.info("Email Token: "+ project.getPropertyValue("nextEmailToken"));

// Get the current mobile token value and add 1 into it 
def newMobileToken = project.getPropertyValue("nextMobileToken").toLong()+1;
project.setPropertyValue("nextMobileToken",newMobileToken+""); 
log.info("Mobile Token: "+ project.getPropertyValue("nextMobileToken"));

